script.script:
var result = null;

if (typeName != null) {

   var input = new Document("granite.search_CIRC_TYPES");
   input.TYPE_NAME = typeName;

   var output = Finder.runFinder("granite.findCircuitTypeForPathByName", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
