script.script:
var debug = "[[1] INVOKE GRANITE INTERFACE VIA 'JMS': CUSTOMER INFORMATION UPDATE] ";
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "START...");
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "INPUT: " + (input != null ? input.toXML() : "") + "...");

      var output = null;

      // Modified on 11.10.2008:
      // - added logic to retry an invocation against Granite if communication fail happens
      var retryMax = 3;
      var retryCounter = 0;

      while (retryCounter < retryMax) {
            retryCounter++;
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "ATTEMPT # " + retryCounter);
            try {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "INVOKING INTERFACE...");
                  output = Global.invokeInterface("ifGranite_jms:XngServicesWR",
                                                                  "CustomerInformationUpdate",
                                                                  input);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "RESULT: " + output + "...");
                  break;
            }
            catch (exc){
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "EXCEPTION: " + exc);
                  // - setting a sleep of 1 min.
                  if (retryCounter < retryMax){
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "SLEEPING FOR 60 SEC...");
                        Global.sleep(60*1000);
                  }
            }
      }

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "END...");
      return output;
