method.script:
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - receivedWOSU = " + this.activityData.toXML());

var receivedWOSU = this.activityData;

this.process.processDocument.latestWOSUId = processSTC.writeWOSUIntoDB(this.process.id, this.activityData);

// update status of the element
this.process.processOrderItem.lineItemStatus = this.activityData.workOrderStatus;
if(processSTC.isLineItemCompleted(this.process.processOrderItem.lineItemStatus)) {
    if(this.process.processOrderItem.isCancel) {
        this.process.processOrderItem.lineItemStatus = "CANCELLED";
    }
    this.process.processOrderItem.completionDate = new Date();
}
this.process.processOrderItem.remarks = this.activityData.workOrderRemarks;
this.process.processOrderItem.save();

if(this.activityData.reservationNumber != null) {
    this.process.processOrder.orderHeader.reservationNumber = this.activityData.reservationNumber;
    this.process.processOrder.save();
}


// this is to properly update the status of the SubService in case of SIP orders migrated
// it has to be applicable if and only if:
// - the element under management is a Service (elementTypeInOrderTree = 'C')
// - the serviceType is Primary SIP (to be check in the configuration table)
// - the order is migrated (isMigrated flag in orderHeader equals to true)
// - the element has only 1 child not provisionable in orchestration table
if(this.process.processOrderItem.elementTypeInOrderTree == 'C' && this.process.processOrder.orderHeader.isMigrated) {
    // the element under management is a service & the order is migrated!

    var childrenListFound = processSTC.findRecordsInOrchTabByOrder_Type_Parent_Seq(this.process.processOrder.id, 'S', this.process.processOrderItem.id);
    if(childrenListFound != null && childrenListFound.length == 1 && !childrenListFound[0].provisionable) {
        // found 1 only child "sub-service" element not provisionable!

        var primarySIPServiceTypeName = ds_ws.getCRMServiceTypeForOMInternalServiceType("SIP/Primary SIP");
        if(primarySIPServiceTypeName != null && primarySIPServiceTypeName == this.process.processOrderItem.serviceType) {
            // checking completed! it's a migrated SIP order

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - updating status of the subService child of the circuit under management");
            var serviceLineItem = Document.readDoc("ds_ws.lineItemDocument", childrenListFound[0].cwDocId);
            if(serviceLineItem != null) {
                serviceLineItem.lineItemStatus = this.process.processOrderItem.lineItemStatus;
                serviceLineItem.completionDate = this.process.processOrderItem.completionDate;
                serviceLineItem.remarks = this.activityData.workOrderRemarks;
                serviceLineItem.save();
            }
            else {
Global.logDebug("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - ERROR! Unable to load document '" + childrenListFound[0].cwDocId + "' child of '" + this.process.processOrderItem.id + "'");
            }
        }
    }
}

ds_ws.updateBundleAndOrderHeaderStatus(this.process.processOrder, this.process.processOrderItem);

// checking lineItemStatus
var isLineItemCompleted = processSTC.isLineItemCompleted(this.process.processOrderItem.lineItemStatus);

if(! isLineItemCompleted) {
    // checking dummy WOSU
    var supportDummyWOSU = Global.getConfigVariable("DUMMY_WOSU_FOR_FAILED_ORDER", "FALSE");
    if(supportDummyWOSU == "TRUE") {
        // to know if WD is managing dummy WOSU, it checks if the status = 'FAILED' and also if
        // the lineItem has been accepted or not by Granite: if a dummy WOSU, the lineItem is not
        // accepted by Granite!
        isLineItemCompleted = (this.process.processOrderItem.lineItemStatus == "FAILED" && (!this.process.processOrderItem.alreadySentToGranite));
    }
}

return (! isLineItemCompleted);
