script.script:
var debugHeader = "interruptProvisioningProcessesForOrder(" + bundleOrderInProvisioning.orderHeader.orderNumber + ") - ";

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Searching all processes for order with internalId: " + bundleOrderInProvisioning.id);

var orchestrationRecordList = processSTC.findRecordsInOrchTabByOrderId_ParentId(bundleOrderInProvisioning.id, null);
for(var i=0; i<orchestrationRecordList.length; i++) {
    // for each element in orchestration table, it terminates first the orchestration process ...
    var processId = orchestrationRecordList[i].orchestrationProcessId;
    if(processId != null) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Checking orchestration process: " + processId);
        var searchDoc = new Document("cwf_pm:ProcessSearch");
        searchDoc.PROCESS_ID = processId;
        var resultProcessFinder = Finder.runFinder("cwf_pm:ProcessFinder", "select", searchDoc);

        if(resultProcessFinder != null && resultProcessFinder.length > 0) {
            var processStatus = resultProcessFinder[0].STATUS;
            if(processStatus == 3 || processStatus == 6) {
                // 3 = COMPLETED
                // 6 = TERMINATED
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Orchestration process: " + processId + " already ended");
            }
            else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Terminating Orchestration process: " + processId);
                Process.stopProcess(processId);
                Process.terminateProcess(processId);
            }
        }
        else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Unable to find Orchestration process: " + processId);
        }
    }

    // ... then the provisioning process
    processId = orchestrationRecordList[i].provisioningProcessId;
    if(processId != null) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Checking provisioning process: " + processId);
        var searchDoc = new Document("cwf_pm:ProcessSearch");
        searchDoc.PROCESS_ID = processId;
        var resultProcessFinder = Finder.runFinder("cwf_pm:ProcessFinder", "select", searchDoc);

        if(resultProcessFinder != null && resultProcessFinder.length > 0) {
            var processStatus = resultProcessFinder[0].STATUS;
            if(processStatus == 3 || processStatus == 6) {
                // 3 = COMPLETED
                // 6 = TERMINATED
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Provisioning process: " + processId + " already ended");
            }
            else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Terminating Provisioning process: " + processId);
                Process.stopProcess(processId);
                Process.terminateProcess(processId);
            }
        }
        else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] " + debugHeader + "Unable to find Provisioning process: " + processId);
        }
    }
}
