script.script:
var inputDoc = new Document("processSTC.STC_GRANITE_EVENT_STORAGE");
inputDoc.processId = processId;
inputDoc.msgType = "OCUA";
inputDoc.cwDocId = null;

var graniteResponseDS = null;
var graniteResponseFound = Finder.runFinder("processSTC.findLatestGraniteEventByProcessAndType", "select", inputDoc);
if(graniteResponseFound != null && graniteResponseFound.length > 0) {
    var xmlMessage = graniteResponseFound[0].graniteMsg;
    var convertedList = Global.getItemsFromXML(xmlMessage, "ds_jms:orderAcknowledgementWithFailure_el");
    if (convertedList != null && convertedList.length > 0) {
        graniteResponseDS = convertedList[0];
    }

}
return graniteResponseDS;
