script.script:
var inputDoc = new Document("processSTC.STC_GRANITE_EVENT_STORAGE");
inputDoc.cwDocId = wosuDocId;
inputDoc.msgType = "WOSU";

var graniteWOSUDataStructure = null;
var graniteWOSUFound = Finder.runFinder("processSTC.findSTC_GRANITE_EVENT_STORAGE", "select", inputDoc);
if(graniteWOSUFound != null && graniteWOSUFound.length > 0) {
    var xmlMessage = graniteWOSUFound[0].graniteMsg;
    var convertedList = Global.getItemsFromXML(xmlMessage, "ds_jms.workOrderStatusUpdate_el");
    if (convertedList != null && convertedList.length > 0) {
        graniteWOSUDataStructure = convertedList[0];
    }

}
return graniteWOSUDataStructure;
