method.script:
var result = false;
var debug = "[cwOnDocValidRule_AccountNumberMandatory] ";

// [A] In case of "Submit Order" request (Order Status in 'New'):
// - this field is not always mandatory, it depends on Order Type
if (document.orderStatus == "New") {

      // Method to check if a field is mandatory according to the Order Type
      result = common.common_checkMandatoryFieldByOrderType(new Array("I", "C", "T"), document.orderType, document.accountNumber);
}

return result;

if (document.actionOnOrder == "Create") {

      if (document.orderType != null) {
            document.orderNumber =
                  ds_ws.common_executeFunction_UniqueOrderNumber("XNG PRODUCTION [USER:STC_TARGET2]",
                                                                                       document.orderType);
      }
      else{
            document.orderNumber = null;
      }
}

var result = false;

try {
    var item = ds_ws.searchOrderMessageByOrderNumber(document.orderNumber);

    if (item != null && item.cwDocId != document.cwDocId) {
        result = true;
    }
}
catch (exc){
    debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + "[cwOnDocValidRule_DocumentDuplicated] EXCEPTION:" + exc.message + "...");
    result = true;
}

return result;

var result = this.validateMeth();
Global.runContentDisplayScript(this, "displayScript");
return result;

if (!this.isValid && Global.isAutosaveEnabled() && !this.model.readonly && this.model.isSavable()) {
  return "UU0256:YN";
}
return null;

// SCRIPT MIGRATION: if null, return false
var $v=
!this.model._isSearch() && this.model._getNoDBOperations();
if ($v == null)
   return false;
else return $v;
