method.script:
// throws error if dependencies are set
return (document.dependencies != null)
// return false;

var res = this.cw$super_onInit();

this.reasonCodes = ds_ws.getReasonCodesForUI(this.model.id);

this.nameValueList = ds_ws.getAllNameValuesByParentElementId(this.model.id);


this.blockNameValueList = ds_ws.getAllBlockNameValuesByParentElementId(this.model.id);

if(this.model.elementTypeInOrderTree == "B") {
    this.lineItemDetailLabel = "ParentLineItem Details";
    this.nvPairLabel = "ParentLineItem Parameters";
    this.blockNVLabel = "ParentLineItem Block Parameters";
}
else if(this.model.elementTypeInOrderTree == "C") {
    this.lineItemDetailLabel = "Service Details";
    this.nvPairLabel = "Service Parameters";
    this.blockNVLabel = "Service Block Parameters";
}
else if(this.model.elementTypeInOrderTree == "S") {
    this.lineItemDetailLabel = "SubService Details";
    this.nvPairLabel = "SubService Parameters";
    this.blockNVLabel = "SubService Block Parameters";
}
else if(this.model.elementTypeInOrderTree == "T") {
    this.lineItemDetailLabel = "Sub-SubService Details";
    this.nvPairLabel = "Sub-SubService Parameters";
    this.blockNVLabel = "Sub-SubService Block Parameters";
}

return res;

var finder = new Finder("ds_ws.msgLogFinderNoShowInput");
finder.searchDocument.workOrderNumber = this.model.workOrderNumber;
finder.searchDocument.fromTime = null;
finder.search();
debugPrintln("finder.list.length = " + finder.list.length);
return finder;

var finder = new Finder(finderName);
finder.searchDocument.cwDocID = this.model.id;
var res = finder.search();
debugPrintln("Res = " + res + "; length = " + res.length);


var finderUI = (isNVFinder ?
                new ds_ws.findNameValueByParentElementId.UserInterface(finder, this) :
                new ds_ws.findBlockNameValuesByParentElementId.UserInterface(finder, this));

var download = (isCSV ?
                new com.conceptwave.system.DownloadCsv(null, this) :
                new com.conceptwave.system.DownloadXls(null, this));

download.fileName = headerFileName + "-" + this.model.lineItemIdentifier + (isCSV ? ".csv" : ".xls");
download.objectToPrint = finderUI;
download.mimeType = (isCSV ? "text/csv" : "text/xsl");
Global.doDownload(download);



return finderUI;
