script.script:
// nv check; it loops in existing NV pair and verifies if it exists also in received one
var serviceTypeForBSIP = ds_ws.getCRMServiceTypeForOMInternalServiceType("SIP/Backup SIP");
var serviceTypeForBSIPMW = ds_ws.getCRMServiceTypeForOMInternalServiceType("SIPMW/Backup SIPMW");
var debugMsg = "compareReceivedNV_BlockCircParamsWithExistingOne (" + lineItemIdentifier + ")";

// 2016-05-30: adding management for STORE & FORWARD List
// checking received attribute:
//     is in SKIP_LIST ?
//        YES: Then, remove the attribute from WD
//        NO: is in STORE_FORWARD_LIST ?
//                YES: do nothing
//                NO:  is in existing order ?
//                        YES: it must have the same value, otherwise, reject
//                        NO:  is in NEW_ATTR_LIST ?
//                                YES: add because it's a new attribute
//                                NO:  reject
//
// checking existing attribute:
//     is in SKIP_LIST ?
//        YES: do nothing
//        NO: is in received order ?
//                YES: does it have the same value?
//                    YES: do nothing
//                    NO:  is in STORE_FORWARD_LIST ?
//                            YES: do nothing
//                            NO:  reject
//                NO: is in NEW_ATTR_LIST ?
//                    YES: do nothing
//                    NO:  reject


if(receivedLineItemContainer.circuitParameters != null) {
    var toBeRemoved = new Array();

    for(var i=0; i<receivedLineItemContainer.circuitParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.circuitParameters[i].circuitParameter.name;
        var found = false;
        var foundWithError = false;
        var lastErrorFound = null;

        // check if the NV pair is in the SKIP_LIST
        var isInSKIPList = ds_ws.isNameInSkipNVPairList(receivedNVName);
        // check if the NV pair is in the STORE_FORWARD_LIST
        var isInStoreAndForwardList = ds_ws.isNameInStoreAndForwardNVPairList(receivedNVName);
        // check if the NV pair is in the NEW_ATTR_LIST
        var isInNewAttrList = ds_ws.isNameInNewAttrNVPairList(receivedNVName);

        if(isInSKIPList) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debugMsg + " Removing NV pair: " + receivedNVName + "#" + i + "#" + receivedLineItemContainer.circuitParameters[i].circuitParameter.orderDataPath);
            toBeRemoved.push(i);
        }
        else {
            if(isInStoreAndForwardList) {
                // do nothing
            }
            else {
                // search nv in existing
                if(existingLineItemContainer.circuitParameters != null) {
                    for(var j=0; j<existingLineItemContainer.circuitParameters.length && !found; j++) {
                        if(receivedNVName == existingLineItemContainer.circuitParameters[j].circuitParameter.name) {
                            // found; comparing the values
                            found = true;
                            var resultArray = compareValues_(receivedLineItemContainer.circuitParameters[i].circuitParameter.value, existingLineItemContainer.circuitParameters[j].circuitParameter.value, receivedNVName, "NV Pair");
                            if(resultArray != null) {
                                found = false;
                                foundWithError = true;
                                lastErrorFound = resultArray;
                            }
                            else {
                                foundWithError = false;
                                lastErrorFound = null;
                            }
                        }
                    }
                }

                if(found) {
                    // it means that it was found with the same value so, nothing to do
                }
                else {
                    if(foundWithError) {
                        // found but it was wrong! adding error
                        validationErrors = ds_ws.appendToValidationErrors(lastErrorFound[0], lastErrorFound[1], validationErrors, debugMsg);
                    }
                    else {
                        if(isInNewAttrList) {
                            // not an issue; it's a new attribute!
                        }
                        else {
                            // not found NV; adding error
                            validationErrors = ds_ws.appendToValidationErrors("AE0065", Global.translateText("AE0065", null, ["NV Pair", receivedNVName, "Service", lineItemIdentifier, "existing", "received"]), validationErrors);
                        }
                    }
                }
            }
        }
    }

    for(var k=toBeRemoved.length-1; k>=0; k--) {
        var index = toBeRemoved[k];
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debugMsg + " Removing NV pair: " + index + "#" + receivedLineItemContainer.circuitParameters[index].circuitParameter.name + "#" + receivedLineItemContainer.circuitParameters[index].circuitParameter.orderDataPath);
        receivedLineItemContainer.circuitParameters[index].circuitParameter.deleteItem();
    }
}


if(existingLineItemContainer.circuitParameters != null) {

    for(var i=0; i<existingLineItemContainer.circuitParameters.length; i++) {
        var existingNVName = existingLineItemContainer.circuitParameters[i].circuitParameter.name;
        var found = false;
        var foundWithError = false;
        var lastErrorFound = null;

        // check if the NV pair is in the SKIP_LIST
        var isInSKIPList = ds_ws.isNameInSkipNVPairList(existingNVName);
        // check if the NV pair is in the STORE_FORWARD_LIST
        var isInStoreAndForwardList = ds_ws.isNameInStoreAndForwardNVPairList(existingNVName);
        // check if the NV pair is in the NEW_ATTR_LIST
        var isInNewAttrList = ds_ws.isNameInNewAttrNVPairList(existingNVName);

        if(isInSKIPList) {
            // do nothing
        }
        else {
            // search nv in received
            if(receivedLineItemContainer.circuitParameters != null) {
                for(var j=0; j<receivedLineItemContainer.circuitParameters.length && !found; j++) {
                    if(existingNVName == receivedLineItemContainer.circuitParameters[j].circuitParameter.name) {
                        // found; comparing the values
                        found = true;
                        var resultArray = compareValues_(receivedLineItemContainer.circuitParameters[j].circuitParameter.value, existingLineItemContainer.circuitParameters[i].circuitParameter.value, existingNVName, "NV Pair");
                        if(resultArray != null) {
                            found = false;
                            foundWithError = true;
                            lastErrorFound = resultArray;
                        }
                        else {
                            foundWithError = false;
                            lastErrorFound = null;
                        }
                    }
                }
            }

            if(found) {
                // it means that it was found with the same value so, nothing to do
            }
            else {
                if(foundWithError) {
                    if(isInStoreAndForwardList) {
                        // do nothing
                    }
                    else {
                        // found but it was wrong and not in Store & Forward list! adding error
                        validationErrors = ds_ws.appendToValidationErrors(lastErrorFound[0], lastErrorFound[1], validationErrors, debugMsg);

                    }
                }
                else {
                    if(isInNewAttrList) {
                        // not an issue; it's a new attribute!
                    }
                    else {
                        // Primary Circuit attribute is added by OM, so if not found, it can due to that
                        if(existingNVName == "Primary Circuit" &&
                            existingLineItemContainer.circuitParameters[i].circuitParameter.action == null &&
                            ((serviceTypeForBSIP != null && existingLineItemContainer.circuit.serviceType == serviceTypeForBSIP) ||
                             (serviceTypeForBSIPMW != null && existingLineItemContainer.circuit.serviceType == serviceTypeForBSIPMW))) {
                            // skipping
                        }
                        else {
                            // not found NV; adding error
                            validationErrors = ds_ws.appendToValidationErrors("AE0065", Global.translateText("AE0065", null, ["NV Pair", existingNVName, "Service", lineItemIdentifier, "received", "existing"]), validationErrors);
                        }
                    }
                }
            }
        }
    }
}



// nv check; it loops in existing NV pair and verifies if it exists also in received one
if(existingLineItemContainer.circuitBlockParameters != null) {
    var missingReceived = 0;
    for(var i=0; i<existingLineItemContainer.circuitBlockParameters.length; i++) {
        var existingNVName = existingLineItemContainer.circuitBlockParameters[i].circuitBlockParameter.name;
        var found = false;
        var foundWithError = false;
        var lastErrorFound = null;
        // search nv in received
        if(receivedLineItemContainer.circuitBlockParameters != null) {
            for(var j=0; j<receivedLineItemContainer.circuitBlockParameters.length && !found; j++) {
                if(existingNVName == receivedLineItemContainer.circuitBlockParameters[j].circuitBlockParameter.name) {
                    // found; comparing the values
                    found = true;
                    var receivedValues = ds_ws.getBlockValuesAsString(receivedLineItemContainer.circuitBlockParameters[j].circuitBlockParameter.id, ";");
                    var existingValues = ds_ws.getBlockValuesAsString(existingLineItemContainer.circuitBlockParameters[i].circuitBlockParameter.id, ";");
                    var resultArray = compareValues_(receivedValues, existingValues, existingNVName, "BlockParameter");
                    if(resultArray != null) {
                        found = false;
                        foundWithError = true;
                        lastErrorFound = resultArray;
                    }
                    else {
                        foundWithError = false;
                        lastErrorFound = null;
                    }
                }
            }
        }

        if(!found) {
            if(foundWithError) {
                // found but it was wrong! adding error
                validationErrors = ds_ws.appendToValidationErrors(lastErrorFound[0], lastErrorFound[1], validationErrors, debugMsg);
            }
            else {
                // not found NV; adding error
                validationErrors = ds_ws.appendToValidationErrors("AE0065", Global.translateText("AE0065", null, ["BlockParameter", existingNVName, "Service", lineItemIdentifier, "received", "existing"]), validationErrors);
                missingReceived++;
            }
        }
    }

    if(receivedLineItemContainer.circuitBlockParameters != null) {
        if((receivedLineItemContainer.circuitBlockParameters.length + missingReceived) != existingLineItemContainer.circuitBlockParameters.length) {
            // it means that there are additional attributes in received that are not in existing! ERROR!
            validationErrors = ds_ws.appendToValidationErrors("AE0066", Global.translateText("AE0066", null, ["Service", lineItemIdentifier, "BlockParameter"]), validationErrors);
        }
    }
}



return validationErrors;

function compareValues_(receivedAttribute, existingAttribute, attributeName, category) {
    var resultArray = null;

    if(receivedAttribute == null) {
        if(existingAttribute != null) {
            resultArray = ["AE0055", Global.translateText("AE0055", null, [category, attributeName, "existing"])];
        }
    }
    else {
        if(existingAttribute == null) {
            resultArray = ["AE0057", Global.translateText("AE0057", null, [category, attributeName, "existing"])];
        }
        else {
            if(! receivedAttribute.toString().equals(existingAttribute.toString())) {
                resultArray = ["AE0056", Global.translateText("AE0056", null, [category, attributeName, receivedAttribute, existingAttribute, "existing"])];
            }
        }
    }

    return resultArray;
}
