var inputDoc = new Document("ds_ws.search_ProductTypeNameMap");
inputDoc.internalProductType = internalProductType;

var resultList = Finder.runFinder("ds_ws.findProductTypeNameMapDocument", "select", inputDoc);

var resultArray = null;

if(resultList != null) {
    for(var i=0; i<resultList.length; i++) {
        if(resultArray == null) {
            resultArray = new Array();
        }
        resultArray.push(resultList[i].crmProductType);
    }
}
return resultArray;
