script.script:
var inputDoc = new Document("ds_ws.search_ValidationMap_serviceType_OrderTypeDoc");
inputDoc.orderType = orderType;
inputDoc.serviceType = serviceType;

var resultList = Finder.runFinder("ds_ws:findLineItemValidationMapByOrderType_ServiceType", "select", inputDoc);

var mandatoryFieldNames = null;

if(resultList != null && resultList.length > 0) {
    var validationMapDocument = resultList[0];

    var arrayOfLeavesAndValues = validationMapDocument.getDocumentAsList(false);
    for(var i=0; i<arrayOfLeavesAndValues.length; i=(i+2)) {
        if(arrayOfLeavesAndValues[i+1] == 1) {
            if(arrayOfLeavesAndValues[i] != "orderType" && arrayOfLeavesAndValues[i] != "serviceType") {
                if(mandatoryFieldNames == null) {
                    mandatoryFieldNames = new Array();
                }

                mandatoryFieldNames.push(arrayOfLeavesAndValues[i]);
            }
        }
    }
}

return mandatoryFieldNames;
