script.script:
var inputDoc = new Document("ds_ws:reasonCode");
inputDoc.cwDocId = null;
inputDoc.parentDocId = parentDocId;

var resultList = Finder.runFinder("ds_ws:reasonCodeFinder", "select", inputDoc);

var resultArray = null;

if(resultList != null) {
    for(var i=0; i<resultList.length; i++) {
        if(resultArray == null) {
            resultArray = new Array();
        }
        resultArray.push(resultList[i].reasonCode);
    }
}
return resultArray;
