script.script:
var result = null;

if (siteName != null || instId != null) {

   var input = new Document("ds_ws.search_SiteForInsertOrder");
   input.SITE_INST_ID = instId;
   input.SITE_HUM_ID = siteName;

   var output = Finder.runFinder("ds_ws.findSiteForInsertOrder", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
