var isFeasForSIPForEnquiry = false;

if(bundleOrder != null) {
    if(bundleOrder.bundles != null) {
        for(var i=0; i<bundleOrder.bundles.length && !isFeasForSIPForEnquiry; i++) {
            isFeasForSIPForEnquiry = (ds_ws.isSIPCircuit(bundleOrder.bundles[i].bundle.serviceType) &&
                                      bundleOrder.bundles[i].bundle.feasibilityType.equalsIgnoreCase("ENQUIRY"));
        }
    }
}

return isFeasForSIPForEnquiry;
