var result = false;
if(orderType != null && orderStatus != null) {
    var finderOrderTypeAndStatusDisabled = new Finder("ds_ws.findOrderTypeAndStatusDisabled");
    finderOrderTypeAndStatusDisabled.searchDocument.orderType  = orderType.toUpperCase();
    finderOrderTypeAndStatusDisabled.searchDocument.orderStatus = orderStatus.toUpperCase();
    var resultList = finderOrderTypeAndStatusDisabled.search();

    if(resultList != null && resultList.length > 0) {
        result = true;
    }
}

return result;
