script.script:
var mandatoryAttributes = ds_ws.getLineItemMandatoryAttribsByOrderType_ServiceType(orderType, serviceType);
if(mandatoryAttributes == null) {
    mandatoryAttributes = ds_ws.getLineItemMandatoryAttribsByOrderType_ServiceType(orderType, "*");
}

var validationErrors = null;

if(mandatoryAttributes != null) {
    var foundWithValue = false;
    for(var i=0; i<mandatoryAttributes.length; i++) {
        foundWithValue = false;

        var attributeValue = lineItem[mandatoryAttributes[i]];

        if(attributeValue == null) {
            if(validationErrors == null) {
                validationErrors = new Array();
            }

            // AE0039: Missing Mandatory Attribute: {0}
            validationErrors.push("AE0039");
            validationErrors.push(Global.translateText("AE0039", null, [mandatoryAttributes[i]]));
        }
    }
}

if(validationErrors != null) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] performValidationOfMandatoryAttributesInLineItem (" + lineItem.lineItemIdentifier + "): Found errors [" + validationErrors + "]");
    failureArray = ds_ws.addValidationErrorsToFailureArrayForSub_UpdInWD(validationErrors, objectId, failureArray, isSubmit);
}

return failureArray;
