script.script:
var mandatoryNVPairNames = ds_ws.getMandatoryNVPairNamesByOrderType_ServiceType(orderType, serviceType);

var validationErrors = null;

if(mandatoryNVPairNames != null) {
    var found = false;
    var foundWithValue = false;
    for(var i=0; i<mandatoryNVPairNames.length; i++) {
        found = false;
        foundWithValue = false;

        for(var j=0; j<nvPairs.length && !found; j++) {
            if(nvPairs[j][0].name == mandatoryNVPairNames[i]) {
                found = true;
                if(nvPairs[j][0].value != null && nvPairs[j][0].value.trim().length > 0) {
                    foundWithValue = true;
                }
            }
        }

        if(!found) {
            if(validationErrors == null) {
                validationErrors = new Array();
            }

            // AE0037: Missing Mandatory NVPair: {0}
            validationErrors.push("AE0037");
            validationErrors.push(Global.translateText("AE0037", null, [mandatoryNVPairNames[i]]));
        }
        else {
            if(!foundWithValue) {
                if(validationErrors == null) {
                    validationErrors = new Array();
                }

                // AE0038: Found Mandatory NVPair with no value: {0}
                validationErrors.push("AE0038");
                validationErrors.push(Global.translateText("AE0038", null, [mandatoryNVPairNames[i]]));
            }
        }
    }
}

if(validationErrors != null) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] performValidationOfMandatoryNVPairs (" + objectId + "): Found errors [" + validationErrors + "]");
    failureArray = ds_ws.addValidationErrorsToFailureArrayForSub_UpdInWD(validationErrors, objectId, failureArray, isSubmit);
}

return failureArray;
