script.script:
// nv check; it loops in existing NV pair and verifies if it exists also in received one
if(receivedLineItemContainer.serviceParameters != null) {
    for(var i=0; i<receivedLineItemContainer.serviceParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.serviceParameters[i].serviceParameter.name;
        var found = false;

        if(existingLineItemContainer.serviceParameters != null) {
            for(var j=0; j<existingLineItemContainer.serviceParameters.length && !found; j++) {
                if(receivedNVName == existingLineItemContainer.serviceParameters[j].serviceParameter.name) {
                    // found; comparing the values
                    found = true;
                    existingLineItemContainer.serviceParameters[j].serviceParameter.value = receivedLineItemContainer.serviceParameters[i].serviceParameter.value;
                    existingLineItemContainer.serviceParameters[j].serviceParameter.action = receivedLineItemContainer.serviceParameters[i].serviceParameter.action;
                }
            }
        }

        if(!found) {
            var position = (existingLineItemContainer.serviceParameters != null ? existingLineItemContainer.serviceParameters.length : 0);
            var containerItemPath = (existingLineItemContainer.serviceParameters != null ? existingLineItemContainer.serviceParameters.orderDataPath : (existingLineItemContainer.orderDataPath + ".serviceParameters"));
            var nvDoc = existingOrder.createOrderItemByPath(containerItemPath + "." + (position+1) + ".serviceParameter");
            nvDoc.name = receivedLineItemContainer.serviceParameters[i].serviceParameter.name;
            nvDoc.value = receivedLineItemContainer.serviceParameters[i].serviceParameter.value;
            nvDoc.action = receivedLineItemContainer.serviceParameters[i].serviceParameter.action;
            nvDoc.parentElementId = existingLineItemContainer.service.id;
        }
    }
}

// bv check; it loops in existing blockValue pair and verifies if it exists also in received one
if(receivedLineItemContainer.serviceBlockParameters != null) {
    for(var i=0; i<receivedLineItemContainer.serviceBlockParameters.length; i++) {
        var receivedNVName = receivedLineItemContainer.serviceBlockParameters[i].serviceBlockParameter.name;
        var found = false;

        if(existingLineItemContainer.serviceBlockParameters != null) {
            for(var j=0; j<existingLineItemContainer.serviceBlockParameters.length && !found; j++) {
                if(receivedNVName == existingLineItemContainer.serviceBlockParameters[j].serviceBlockParameter.name) {
                    // found; delete the existing
                    found = true;
                    ds_ws.deleteAllBlockValuesByParentId(existingLineItemContainer.serviceBlockParameters[j].serviceBlockParameter.id, existingOrder.id);

                    var receivedBlockValuesArray = ds_ws.getBlockValuesAsArray(receivedLineItemContainer.serviceBlockParameters[i].serviceBlockParameter.id);
                    if(receivedBlockValuesArray != null) {
                        for(var k=0; k<receivedBlockValuesArray.length; k++) {
                             createBlockValue_(receivedBlockValuesArray[k].blockValue, existingLineItemContainer.serviceBlockParameters[j].serviceBlockParameter.id, existingOrder);
                        }
                    }
                }
            }
        }

        if(!found) {
            var position = (existingLineItemContainer.serviceBlockParameters != null ? existingLineItemContainer.serviceBlockParameters.length : 0);
            var containerItemPath = (existingLineItemContainer.serviceBlockParameters != null ? existingLineItemContainer.serviceBlockParameters.orderDataPath : (existingLineItemContainer.orderDataPath + ".serviceBlockParameters"));
            var bvDoc = existingOrder.createOrderItemByPath(containerItemPath + "." + (position+1) + ".serviceBlockParameter");
            bvDoc.name = receivedLineItemContainer.serviceBlockParameters[i].serviceBlockParameter.name;
            bvDoc.action = receivedLineItemContainer.serviceBlockParameters[i].serviceBlockParameter.action;
            bvDoc.parentElementId = existingLineItemContainer.service.id;

            var receivedBlockValuesArray = ds_ws.getBlockValuesAsArray(receivedLineItemContainer.serviceBlockParameters[i].serviceBlockParameter.id);
            if(receivedBlockValuesArray != null) {
                for(var k=0; k<receivedBlockValuesArray.length; k++) {
                     createBlockValue_(receivedBlockValuesArray[k].blockValue, bvDoc.id, existingOrder);
                }
            }
        }
    }
}

return validationErrors;


function createBlockValue_(value, parentDocId, existingOrder) {
    var blockValueDoc = new Document("ds_ws:blockValue");
    blockValueDoc.parentDocId = parentDocId;
    blockValueDoc.cwOrderId = existingOrder.id;
    blockValueDoc.blockValue = value;
    blockValueDoc.save();
}
