script.script:
var result = null;

if (bandwidthName != null) {

   var input = new Document("granite.search_CIRC_BW");
   input.BANDWIDTH = bandwidthName;

   var output = Finder.runFinder("granite.findCircBWByName", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
