script.script:
var result = null;

if (pathName != null) {

   var input = new Document("granite:search_CIRC_PATH_INST");
   input.CIRC_PATH_HUM_ID = pathName;

   var output = Finder.runFinder("granite:findPathByPathName", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
