script.script:
var result = null;

if (resourceName != null) {

   var input = new Document("granite.search_RESOURCE_INST");
   input.NAME = resourceName;

   var output = Finder.runFinder("granite.findResourceByNameAndId", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
