script.script:
var result = null;

if (siteInstId != null || siteName != null) {

   var input = new Document("granite:search_SITE_INST");
   input.INST_ID = siteInstId;
   input.SITE_HUM_ID = siteName;

   var output = Finder.runFinder("granite:findSiteByNameAndId", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
