script.script:
var debug = "[MANAGE GRANITE MESSAGE VIA 'JMS': WOSU] ";

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Received: " + (input != null ? input.toXML() : "'NULL'") + "...");

var logDoc = UserProfile.getMsgLogDoc();

if (input != null) {
    if(input.workOrderNumber != null && input.crmOrderNumber != null) {

        var lineItem = ds_ws.searchLineItemByCRMOrderNumberAndWONumber(input.crmOrderNumber, input.workOrderNumber);

        if(lineItem != null) {
            var orchestrationRecord = processSTC.getRecordInOrchestrationTableByLineItemId(lineItem.id);
            if(orchestrationRecord != null) {
                var provisioningProcessId = orchestrationRecord.provisioningProcessId;
                if(provisioningProcessId != null) {

                    var processInstance = Process.getProcessInfo(provisioningProcessId);
                    if(processInstance != null) {
                        if(processInstance.processStatus == 8) {
                            // the process is in status 8, so STALE; resume it
                            debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Resuming Stale process " + provisioningProcessId);
                            Process.resumeAllStaleProcesses(provisioningProcessId);
                        }
                    }

                    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Sending WorkOrderStatusUpdate for <" + input.crmOrderNumber + "," + input.workOrderNumber + "> to process " + provisioningProcessId);
                    Process.sendMessageToProcess(provisioningProcessId, null, "ifGranite_jms.XngServicesNotificationToProcesses/WorkOrderStatusUpdate", input);
                }
                else {
                    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Unable to find Provisioning Process for received granite WOSU for <" + input.crmOrderNumber + "," + input.workOrderNumber + ">; starting standalone process");

                    var wosuID = processSTC.writeWOSUIntoDB("-1", input);

                    var wosuProcessDocument = new Document("processSTC.STC_PROCESS_DOCUMENT");
                    wosuProcessDocument.latestWOSUId = wosuID;
                    wosuProcessDocument.firstPassWOSU = "Y";

                    var wosuPID = Process.startProcess("processSTC.sendWOSUToCRM", wosuProcessDocument);
                    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Unable to find Provisioning Process for received granite WOSU for <" + input.crmOrderNumber + "," + input.workOrderNumber + ">; started standalone process with PID " + wosuPID);
                }
            }
            else {
                debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Unable to find process for <" + input.crmOrderNumber + "," + input.workOrderNumber + ">");
            }
        }
        else {
            debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Unable to find LineItem for received granite WOSU for <" + input.crmOrderNumber + "," + input.workOrderNumber + ">; starting standalone process");

            var wosuID = processSTC.writeWOSUIntoDB("-1", input);

            var wosuProcessDocument = new Document("processSTC.STC_PROCESS_DOCUMENT");
            wosuProcessDocument.latestWOSUId = wosuID;
            wosuProcessDocument.firstPassWOSU = "Y";

            var wosuPID = Process.startProcess("processSTC.sendWOSUToCRM", wosuProcessDocument);
            debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Unable to find LineItem for received granite WOSU for <" + input.crmOrderNumber + "," + input.workOrderNumber + ">; started standalone process with PID " + wosuPID);
        }
    }
    else {
        debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Received granite WOSU with crmOrderNumber NULL or workOrderNumber NULL!!!");
    }

    if(logDoc != null) {
        logDoc.userData1 = input.crmOrderNumber;
        logDoc.userData2 = input.workOrderNumber;
        logDoc.save();
    }

}
else{
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING - Interface invoked with input NULL...");
}
