method.script:
var resultList = Finder.runFinder("processSTC.findOrdersRejectedForLock", "select", null);
if(resultList != null && resultList.length > 0) {

    for(var i=0; i<resultList.length; i++) {
        if(i == 0) {
            this.process.processDocument.orderNumberList = resultList[i].orderNumber;
        }
        else {
            this.process.processDocument.orderNumberList = this.process.processDocument.orderNumberList + ";" + resultList[i].orderNumber;
        }
    }

}
else {
    this.process.processDocument.orderNumberList = null;
}


this.process.processDocument.index = 0;

var orderNumberToProcess = this.process.processDocument.orderNumberList.split(";")[this.process.processDocument.index];

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Starting Processing record with orderNumberToProcess " + orderNumberToProcess);

var orderHeader = ds_ws.getBundleOrderByOrderNumber(orderNumberToProcess);
if(orderHeader == null) {
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Unable to find orderHeader for orderNumber '" + orderNumberToProcess + "'");
}
else {
    try {
        processSTC.restartProvisioningProcessForOrder(orderHeader);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Restarted Provisioning Process for order with orderNumber '" + orderNumberToProcess + "'");
    }
    catch(exc) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Error while restarting process for order with orderNumber'" + orderNumberToProcess + "' - Exc: " + exc.message);
    }
}

// removing the orderNumber from the list
processSTC.removeOrderToFailedOrdersForLockList(orderNumberToProcess);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Completed processing record with orderNumberToProcess " + orderNumberToProcess);

// sleep for 180 secs, so 3 minutes
var sleepTime = 180;

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.id + "] - Sleeping for " + sleepTime + " before processing the next record");

return sleepTime;

var existAnotherRecord = (this.process.processDocument.orderNumberList != null &&
                          this.process.processDocument.orderNumberList.split(";").length > this.process.processDocument.index);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Another record found? " + existAnotherRecord);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - OrderNumberList: " + this.process.processDocument.orderNumberList);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Index: " + this.process.processDocument.index);

// existAnotherRecord = false;
return existAnotherRecord;

var configExecuteGlobalReprocessFailed = Global.getConfigVariable("EXECUTE_GLOBAL_REPROCESS_FOR_LOCK", "FALSE").toUpperCase();
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - EXECUTE_GLOBAL_REPROCESS_FOR_LOCK = " + configExecuteGlobalReprocessFailed);

return (configExecuteGlobalReprocessFailed == "TRUE");
