var resultList = Finder.runFinder("processSTC.findRunningProcessesForUpdatedOrders", "select", null);
if(resultList != null && resultList.length > 0) {

    for(var i=0; i<resultList.length; i++) {
        var processInfo = Process.getProcessInfo(resultList[i].processId);

        if(processInfo.processStatus == 1) {
            processSTC.changeProcessStatusViaSQL(resultList[i].processId, processInfo.bucket, 2);
        }

        Process.terminateProcess(resultList[i].processId);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Terminated Process " + resultList[i].processId);

    }

}
