method.script:
var searchDoc = new Document("cwt_common.docID");
searchDoc.cwDocID = this.process.processDocument.currentHeaderId;
var resultFinder = Finder.runFinder("ds_ws.findInsertFeasibilityOrder", "select", searchDoc);
if(resultFinder != null && resultFinder.length > 0) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Processing record with id : " + this.process.processDocument.currentHeaderId);

    var generatedOrder = ds_ws.generateFeasibilityOrderFromGUI(resultFinder[0]);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Generated Feasibility Order with id : " + generatedOrder.id);

    var processId = ds_ws.startProcessingGeneratedFeasibilityOrder(generatedOrder);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Started process with id : " + processId);

    resultFinder[0].generatedOrderNumber = generatedOrder.orderHeader.orderNumber;
    resultFinder[0].save();
}
else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - GRAVE!!!! Not found bulk record with id: " + this.process.processDocument.currentHeaderId);
}

var finder = new Finder("ds_ws.findUploadedBulkFeasibilityOrder");
finder.searchDocument.jobId = this.process.processDocument.jobId;
finder.setViewByName("findUploadedFeasibilityOrderView", true);
var resultList = finder.search();

var anotherRecordToProcess = false;
this.process.processDocument.currentHeaderId = null;
if(resultList != null && resultList.length > 0) {
    if(this.process.processDocument.countProcessed < resultList.length) {
        this.process.processDocument.currentHeaderId = resultList[this.process.processDocument.countProcessed].cwDocId;
        anotherRecordToProcess = true;
    }
}

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Another Record to process: " + anotherRecordToProcess + "; currentHeaderId = " + this.process.processDocument.currentHeaderId);

return anotherRecordToProcess;

this.process.processDocument.countProcessed = 0;
this.process.processDocument.currentHeaderId = null;

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Start for jobId: " + this.process.processDocument.jobId);
