method.script:
// configured time for sleeping
var submitSleep = Global.getConfigVariable("SUBMITSLEEP", "10");

// if downtime
var ifDelay = Global.getInterfaceDelay("ifEAI_OCUA_17122008:OCUA");
ifDelay = ((ifDelay != null) ? ifDelay : "10") ;

// sleepTime of the process = max(submitSleep, ifDelay);
var sleepTime = ((submitSleep > ifDelay) ? submitSleep : ifDelay);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.id + "] - sleep for : " + sleepTime + " secs -- {" + submitSleep + ";" + ifDelay + "}");

return sleepTime;

this.process.processDocument.errorType = null;

try {
    var graniteResponse = processSTC.readGraniteResponseFromDB(this.process.id);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - submit/updateOrder Granite response = '" + graniteResponse.toXML() + "'");

    // map graniteResponse into data sent to CRM
    var request = new DataStructure("ifEAI_OCUA_17122008.processRequest_el");
    request.OrderCreateUpdateAck.MasterOrderNumber = this.process.processOrder.orderHeader.orderNumber;
    request.OrderCreateUpdateAck.ServiceOrderNumber = this.process.processOrderItem.workOrderNumber;
    request.OrderCreateUpdateAck.ServiceOrderStatus = this.process.processOrderItem.lineItemStatus;
    request.OrderCreateUpdateAck.MasterOrderStatus = this.process.processOrder.orderHeader.orderStatus;
    request.OrderCreateUpdateAck.LineItemIdentifier = this.process.processOrderItem.lineItemIdentifier;

    if(graniteResponse.failure != null && graniteResponse.failure.length > 0) {
        for(var i=0; i<graniteResponse.failure.length; i++) {
            var failure = new DataStructure("ifEAI_OCUA_17122008.OrderFailure");
            failure.ErrorCode = graniteResponse.failure[i].errorCode;
            failure.ErrorDescription = graniteResponse.failure[i].errorDescription;
            failure.ErrorTime = graniteResponse.failure[i].errorTime
            failure.ErrorType = graniteResponse.failure[i].errorType
            failure.FunctionName = "Submit/Update Order to Granite";
            failure.ObjectId = this.process.processOrderItem.lineItemIdentifier;
            failure.ProcessInstanceld = null;
            failure.SourceErrorCode = null;
            failure.SystemName = graniteResponse.failure[i].systemName
            failure.TargetRecored = null;
            failure.UserId = null;
            request.OrderCreateUpdateAck.Failure[i] = failure;
        }
    }

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - orderAck to CRM = '" + request.toXML() + "'");

    // sending data to EAI
    var response = processSTC.sendDataToInterface(request,
                                                  "ifEAI_OCUA_17122008:OCUA",
                                                  "processRequest",
                                                  true,
                                                  this.process.id,
                                                  "SendOrderAckToCRM",
                                                  this.process.processOrder.orderHeader.orderNumber,
                                                  this.process.processOrderItem.workOrderNumber);
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - CRM orderAck response = '" + response.toXML() + "'");
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
        debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - I/F ifEAI_OCUA_17122008:OCUA/processRequest error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Interface error while sending orderAck to EAI");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Application error while sending orderAck to EAI");
}

return isApplicationError;
