method.script:
// configured time for sleeping
var submitSleep = Global.getConfigVariable("SUBMITSLEEP", "10");

var isB2BOrder = (this.processOrder != null ? processSTC.isB2BOrder(this.processOrder.orderHeader) : false);
var interfaceName = (isB2BOrder ? "ifEAI_WOSU_17122008:receiveWorkOrderStatusUpdate_B2B_PortType" : "ifEAI_WOSU_17122008:receiveWorkOrderStatusUpdate_PortType");

// if downtime
var ifDelay = Global.getInterfaceDelay(interfaceName);
ifDelay = ((ifDelay != null) ? ifDelay : "10") ;

// sleepTime of the process = max(submitSleep, ifDelay);
var sleepTime = ((submitSleep > ifDelay) ? submitSleep : ifDelay);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.id + "] - sleep for : " + sleepTime + " secs -- {" + submitSleep + ";" + ifDelay + "}");

return sleepTime;

this.process.processDocument.errorType = null;

try {
    var graniteWOSU = processSTC.readWOSUFromDB(this.process.processDocument.latestWOSUId);

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - WOSU = '" + graniteWOSU.toXML() + "'");

    var existOrder = (this.process.processOrder != null);
    var existOrderFromProcessDocument = false;
    var orderFromProcessDocument = null;
    var orderItemFromProcessDocument = null;

    var hasLineItemToSkipWOSU = false;

    if(!existOrder) {
        existOrderFromProcessDocument = (this.process.processDocument.lineItemId != null);
        orderItemFromProcessDocument = (existOrderFromProcessDocument ? Document.readDoc("ds_ws:lineItemDocument", this.process.processDocument.lineItemId) : null);
        orderFromProcessDocument = (orderItemFromProcessDocument != null ? Order.getOrderById(orderItemFromProcessDocument.orderId) : null);
    }
    else {
        hasLineItemToSkipWOSU = processSTC.hasLineItemToSkipWOSU(this.process.processOrderItem,
                                                                 ds_ws.isLegacyService(this.process.processOrder.bundles[0]),
                                                                 graniteWOSU.taskOperation,
                                                                 processSTC.isB2BOrder(this.process.processOrder.orderHeader));
    }

    var woStatus = graniteWOSU.workOrderStatus;
    if(existOrder) {
        if (woStatus == "COMPLETED") {
            if(this.process.processOrderItem.isCancel) {
                woStatus = "CANCELLED";
            }
        }
    }
    else if(existOrderFromProcessDocument) {
        if (woStatus == "COMPLETED") {
            if(orderItemFromProcessDocument.isCancel) {
                woStatus = "CANCELLED";
            }
        }
    }

    // map graniteWOSU into data sent to CRM
    // map graniteResponse into data sent to CRM
    var request = new DataStructure("ifEAI_WOSU_17122008.receiveWorkOrderStatusUpdate_el");
    processSTC.fillHeaderForOrderACK_WOSU(request.workOrderStatusUpdateRequest.header, "WorkOrderStatusUpdate");

    // SMR 1639 - setting the CROMOrderNumber equals to primaryOrderNumber if it's set
    var orderNumber = graniteWOSU.crmOrderNumber;
    if(existOrder) {
        orderNumber = (this.process.processOrder.orderHeader.primaryOrderNumber != null ? this.process.processOrder.orderHeader.primaryOrderNumber :
                                                                                          this.process.processOrder.orderHeader.orderNumber);
    }

    // STCSUP-102 - in case the WO is completed and the serviceStatus = "READY TO ACT", set the serviceStatus to "LIVE"
    var receivedServiceStatus = graniteWOSU.serviceStatus;
    if(graniteWOSU.workOrderStatus == "COMPLETED" && graniteWOSU.serviceStatus == "READY TO ACT") {
        receivedServiceStatus = "LIVE";
    }

    // checking if it's related to B2B order
    var isB2BOrder = (existOrder ? processSTC.isB2BOrder(this.process.processOrder.orderHeader) :
                                   (existOrderFromProcessDocument ? processSTC.isB2BOrder(orderFromProcessDocument.orderHeader) : false));


    var crmOrderStatus = (existOrder ? this.process.processOrder.orderHeader.orderStatus :
                                       (existOrderFromProcessDocument ? orderFromProcessDocument.orderHeader.orderStatus : graniteWOSU.workOrderStatus));
    request.workOrderStatusUpdateRequest.body.crmOrderNumber = orderNumber;
    request.workOrderStatusUpdateRequest.body.crmOrderStatus = (crmOrderStatus == "New" ? "ORDERED" : crmOrderStatus);
    request.workOrderStatusUpdateRequest.body.businessUnit = (existOrder ? this.process.processOrder.orderHeader.businessUnit : graniteWOSU.businessUnit);

    if(isB2BOrder) {
        request.workOrderStatusUpdateRequest.body.fulfillmentMode = graniteWOSU.fulfillmentMode;
    }

    request.workOrderStatusUpdateRequest.body.reservationNumber = graniteWOSU.reservationNumber;
    request.workOrderStatusUpdateRequest.body.lineItemIdentifier = (existOrder ? this.process.processOrderItem.lineItemIdentifier :
                                                                                 (existOrderFromProcessDocument ? orderItemFromProcessDocument.lineItemIdentifier : -1));
    request.workOrderStatusUpdateRequest.body.lineItemStatus = receivedServiceStatus;
    request.workOrderStatusUpdateRequest.body.lineItemType = (existOrder ? this.process.processOrderItem.lineItemType :
                                                                           (existOrderFromProcessDocument ? orderItemFromProcessDocument.lineItemType : "-"));
    request.workOrderStatusUpdateRequest.body.remarks = (existOrder ? this.process.processOrderItem.remarks :
                                                                      (existOrderFromProcessDocument ? orderItemFromProcessDocument.remarks : null));
    request.workOrderStatusUpdateRequest.body.workOrderName = (existOrder ? this.process.processOrderItem.workOrderNumber : graniteWOSU.workOrderNumber);
    request.workOrderStatusUpdateRequest.body.workOrderStatus = woStatus;
    request.workOrderStatusUpdateRequest.body.workOrderRemarks = graniteWOSU.workOrderRemarks;
    request.workOrderStatusUpdateRequest.body.workOrderProblemDescription = graniteWOSU.workOrderProblemDescription;
    request.workOrderStatusUpdateRequest.body.workOrderServiceCompletionStatus = graniteWOSU.workOrderServiceCompletionStatus;
    request.workOrderStatusUpdateRequest.body.taskNumber = graniteWOSU.taskNumber;
    request.workOrderStatusUpdateRequest.body.taskName = graniteWOSU.taskName;
    request.workOrderStatusUpdateRequest.body.taskAllocatedTime = graniteWOSU.taskAllocatedTime;
    request.workOrderStatusUpdateRequest.body.taskDescription = graniteWOSU.taskDescription;
    request.workOrderStatusUpdateRequest.body.taskOperation = graniteWOSU.taskOperation;
    request.workOrderStatusUpdateRequest.body.taskPriority = graniteWOSU.taskPriority;
    // 2015-06-15: according to Rao email, renamed attribute in Granite JMS event from taskServiceStatus to serviceStatus
    // request.workOrderStatusUpdateRequest.body.taskServiceStatus = graniteWOSU.taskServiceStatus;
    request.workOrderStatusUpdateRequest.body.taskServiceStatus = receivedServiceStatus;
    request.workOrderStatusUpdateRequest.body.taskStatusCode = graniteWOSU.taskStatusCode;
    request.workOrderStatusUpdateRequest.body.taskDeptOf = graniteWOSU.taskDeptOf;
    request.workOrderStatusUpdateRequest.body.taskRemarks = graniteWOSU.taskRemarks;
    request.workOrderStatusUpdateRequest.body.taskJeopardyStatus = graniteWOSU.taskJeopardyStatus;
    request.workOrderStatusUpdateRequest.body.woStage = graniteWOSU.woStage;
    request.workOrderStatusUpdateRequest.body.icmsTaskId = graniteWOSU.icmsTaskId;

    if(graniteWOSU.parameters != null && graniteWOSU.parameters.length > 0) {
        for(var i=0; i<graniteWOSU.parameters.length; i++) {
            var parameter = new DataStructure("ifEAI_WOSU_17122008.addParameters");
            parameter.name = graniteWOSU.parameters[i].name;
            parameter.value = graniteWOSU.parameters[i].value;

            // 2019-06-03: workaround to fix the case in which CIM-E is sending the NV Pair "FeasibilityStatus" without any value!
            if(parameter.name == "FeasibilityStatus") {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus' ....");

                if(parameter.value == null) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; null value, so continue the check");

                    if(existOrder) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; related to a real order!");

                        if(this.process.processOrder.orderHeader.orderType == "F") {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; related to a real 'F' order!");

                            var isLineItemCompleted = processSTC.isLineItemCompleted(this.process.processOrderItem.lineItemStatus) ||
                                                      (processSTC.isLineItemOnHeld(this.process.processOrderItem.lineItemStatus) && (this.process.processOrder.orderHeader.orderType == "F"));
                            if(isLineItemCompleted) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; LineItem is COMPLETED! Checking UDA value");

                                var udaValueFound = granite.getUDAValueForWorkOrder(this.process.processOrderItem.workOrderNumber, "Feasibility Status", "Feasibility Info");
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; UDA VAlue is '" + udaValueFound + "'");

                                if(udaValueFound != null) {
                                    if(udaValueFound.toUpperCase() == "YES" || udaValueFound.toUpperCase() == "PARTIAL FEASIBLE" || udaValueFound.toUpperCase() == "NA") {
                                        parameter.value = "Yes";
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; converted to 'Yes'");
                                    }
                                    else {
                                        parameter.value = "No";
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; converted to 'No'");
                                    }
                                }
                                else {
                                    // do nothing because it's an issue on TMM side as per Dharvesh email on 2019-06-03!
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; Found NULL as UDA VAlue!!!");
                                }
                            }
                            else {
                                // do nothing because not completed
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; LineItem is NOT COMPLETED so do nothing!");
                            }
                        }
                        else {
                            // do nothing because it's not an "F" order
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; Order is not 'F' so do nothing");
                        }
                    }
                    else {
                        // do nothing because it's not a real order managed by EOC
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; WOSU not linked to a real Order in EOC");
                    }
                }
                else {
                    // do nothing because the value is not null
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Checking NV pair 'FeasibilityStatus'; NOT NULL value so OK!");
                }
            }
            // 2019-06-03: end of the workaround!

            request.workOrderStatusUpdateRequest.body.lineItemValuePairs.addParameters[i] = parameter;
        }
    }

    if(!existOrder || this.process.processOrder.orderHeader.internalOrder == "N") {
        if(!hasLineItemToSkipWOSU) {

debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - WOSU to " + (isB2BOrder ? "CRM B2B" : "CRM-D") + "= '" + request.toXML() + "'");

            var interfaceName = (isB2BOrder ? "ifEAI_WOSU_17122008:receiveWorkOrderStatusUpdate_B2B_PortType" : "ifEAI_WOSU_17122008:receiveWorkOrderStatusUpdate_PortType");

            // sending data to EAI
            var response = processSTC.sendDataToInterface(request,
                                                          interfaceName,
                                                          "receiveWorkOrderStatusUpdate",
                                                          true,
                                                          this.process.id,
                                                          "SendWOSUToCRM",
                                                          (existOrder ? this.process.processOrder.orderHeader.orderNumber : graniteWOSU.crmOrderNumber),
                                                          (existOrder ? this.process.processOrderItem.workOrderNumber : graniteWOSU.workOrderNumber));
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - CRM WOSU response = '" + response.toXML() + "'");
        }
        else {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - WOSU NOT sent to CRM because found configuration to skip WOSU; orderNumber = " + this.process.processOrder.orderHeader.orderNumber);
        }
    }
    else {
        if(existOrder) {
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - WOSU NOT sent to CRM because it's related to an internal order; orderNumber = " + this.process.processOrder.orderHeader.orderNumber);
        }
    }
}
catch(exc) {
    if(exc.message.indexOf("XE0023") >= 0 || exc.message.indexOf("XE0002")) {
        debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - I/F ifEAI_WOSU_17122008:WOSU/WorkOrderStatusUpdate error: " + exc.message);
        this.process.processDocument.errorType = "Interface";
    }
    else {
        this.process.processDocument.errorType = "Application";
    }
}

var isInterfaceError = (this.process.processDocument.errorType == "Interface");
if(isInterfaceError) {
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Interface error while sending WOSU to EAI");
}

return isInterfaceError;

var isApplicationError = (this.process.processDocument.errorType == "Application");
if(isApplicationError) {
    debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - Application error while sending WOSU to EAI");
}

return isApplicationError;
