method.script:
if(this.process.processDocument.isSubmit == "Y") {
    processSTC.sendMailForError("Caught error in submit read process " + this.process.id, Global.getConfigVariable("MAIL_RECEIVER_FOR_ALERTS"));
}
else {
    processSTC.sendMailForError("Caught error in update read process " + this.process.id, Global.getConfigVariable("MAIL_RECEIVER_FOR_ALERTS"));
}

var submitSleep = Global.getConfigVariable("SUBMITSLEEP", "10");
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.id + "] - sleep for : " + submitSleep + " secs");

return submitSleep;

this.process.processDocument.firstPassSubRead  = "N";

this.process.processDocument.countSubRead++;

if(this.process.processDocument.countSubRead > 300) {
    // generate error ack to avoid to put process in error status due too many iterations
    var inputJMS = new DataStructure("ds_jms.orderAcknowledgementWithFailure_el");
    inputJMS.crmOrderNumber = this.process.processOrder.orderHeader.orderNumber;
    inputJMS.workOrderNumber = this.process.processOrderItem.workOrderNumber;
    inputJMS.lineItemIdentifier = this.process.processOrderItem.lineItemIdentifier;
    inputJMS.status = "Rejected";
    inputJMS.xngOrderNumber = null;

    var failure = new DataStructure("ds_jms.failure");
    failure.errorCode = "AE0041";
    failure.errorDescription = Global.translateText("AE0041");
    failure.errorTime = common.common_currentDateAsStringDDMMYYYYHH24MISS();
    failure.errorType = "Functional";
    failure.systemName = "Expediter";

    inputJMS.failure[0] = failure;


    ifGranite_jms.manageGraniteMsg_SubmitUpdateOrderResponse(inputJMS);
}
