script.script:
var inputDoc = new Document("processSTC.STC_ORDER_ORCHESTRATION");
inputDoc.cwOrderId = cwOrderId;

var allProcessesCompleted = true;
var orchestrationRecordList = Finder.runFinder("processSTC.findSTC_ORDER_ORCHESTRATION", "select", inputDoc);
if(orchestrationRecordList != null && orchestrationRecordList.length > 0) {
    for(var i=0; i<orchestrationRecordList.length && allProcessesCompleted; i++) {
        var orchestrationProcessId = orchestrationRecordList[i].orchestrationProcessId;
        if(orchestrationProcessId != null) {
            allProcessesCompleted = processSTC.checkIfProcessIsCompleted(orchestrationProcessId);
            if(!allProcessesCompleted) {
debugPrintln("checkAllProcessesInOrchestrationTableAreCompleted for Order " + cwOrderId + " - Orchestration process " + orchestrationProcessId + " not completed");
            }
        }

        if(allProcessesCompleted) {
            var provisioningProcessId = orchestrationRecordList[i].provisioningProcessId;
            if(provisioningProcessId != null) {
                allProcessesCompleted = processSTC.checkIfProcessIsCompleted(provisioningProcessId);
                if(!allProcessesCompleted) {
                    debugPrintln("checkAllProcessesInOrchestrationTableAreCompleted for Order " + cwOrderId + " - Provisioning process " + provisioningProcessId + " not completed");
                }
            }
        }
    }
}


debugPrintln("checkAllProcessesInOrchestrationTableAreCompleted for Order " + cwOrderId + " = " + allProcessesCompleted);
return allProcessesCompleted;
