script.script:
var inputDoc = new Document("processSTC.STC_INTERNAL_NAME_MAPPING");
inputDoc.objectName = objectName;
inputDoc.externalName = attributeName;

var internalAttributeName = null;

var resultList = Finder.runFinder("processSTC.findInternalAttributeNameForObject", "select", inputDoc);
if(resultList != null && resultList.length > 0) {
    internalAttributeName = resultList[0].internalName;
}
return internalAttributeName;
