script.script:
var isToForward = false;

if(orderNumber != null) {
    var searchDocument = new Document("ds_ws:search_orderNumber");
    searchDocument.orderNumber = orderNumber;

    var resultList = Finder.runFinder("ds_ws:findOrderToForwardToCRMB2B", "select", searchDocument);
    if(resultList != null && resultList.length > 0) {
        isToForward = true;
    }
}
debugPrintln("[" + common.common_currentDateAsStringDDMMYYYYHH24MISS() + "] isOrderToForwardToCRMB2B <" + orderNumber + ">: " + isToForward);

return isToForward;
