method.script:
// Modified on 06.01.2009:
// - check if the Order Status is 'ARCHIVED': in this case is rejected;
return (input.orderStatus == null || input.orderStatus.toUpperCase() != "ARCHIVE");

var selectList = this.result.selected;
var finder = this.model;

      // Action: Change "Order #" (in case the order is modified on Granite)
      var order = Order.getOrderById(selectList[0].orderId);
      var window = this.confirmObject;
      if (window.orderNumber == null ||
            window.orderNumber.trim() == ""){
            Global.throwException("Action not performed: please type in a valid 'Order #' field!");
      }
      else if (window.orderNumber.trim() == window.oldOrderNumber.trim()){
            Global.throwException("Action not performed: new 'Order #' field equal to previous one!");
      }
      else{
            order.orderMessage.orderNumber = window.orderNumber.trim();
            var validation = order.validate(10, true);
            if (validation != null && validation.length > 0) {
                  for (var j = 0; j < validation.length; j = j + 2) {
                        if (validation[j] == "AE0001") {
                              Global.throwException("Action not performed: new 'Order #' field already used by another order!");
                        }
                  }
            }
            order.save();
            Global.showUserMessage("Action performed: 'Order #' field set to '" + order.orderMessage.orderNumber + "'!");
            this.searchAction();
      }

var selectList = this.result.selected;

      if (selectList != null && selectList.length > 0) {
            if (selectList.length == 1) {
                  var item = selectList[0];
                  var window = new Document("ds_ws:search_ChangeOrderNumber");
                  window.oldOrderNumber = item.orderNumber;
                  return window;
            }
            else{
                  Global.throwException("Action not performed: please select just one order!");
            }
      }
      else{
            Global.throwException("Action not performed: no order has been selected!");
      }

var result = this.finder_ChangeOrderNumber();
if(result instanceof Finder) {
  Global.runContentDisplayScript(result.searchDocument, "displayScript");
} else {
  Global.runContentDisplayScript(this, "displayScript");
}

return result;

var selectList = this.result.selected;
var finder = this.model;

      // Action: Archive Order
      var order = Order.getOrderById(selectList[0].orderId);

      // -----------------------------------------------------
      // USING THIS BECAUSE OF EXPEDITER BUG [TR # 406109]:
      // - the request is pushed forward vs. Granite through JMS
      // -----------------------------------------------------

      var pid = Process.startProcess("processSTC:mainSTCProcess", order.id);
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Started Process with pid " + pid);

      Global.commitTransaction("ORDER");

      var countSleeping = 0;

      var processCompleted = false;
      while(!processCompleted) {
            // sleeps 5 seconds and checks the status of the process ...
//debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Sleeping before checking the status of Process " + pid);
            processSTC.timeDelay("GRANITE_RESPONSE_DELAY_TIME");

            processCompleted = processSTC.checkIfProcessIsCompleted(pid);
            countSleeping++;
      }

      // reload the order because modified by the process
      var cworderId = order.id;
      order = Order.getOrderById(cworderId);

      if (order.orderACK.Status == "SUCCESS") {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + "<RE-SUBMIT 'IN HOLD' ORDER> WITH 'ORDER #': '" + input.message.OrderNumber + "' WORKED CORRECTLY!");
            Global.logDebug("<RE-SUBMIT 'IN HOLD' ORDER> WITH 'ORDER #': '" + input.message.OrderNumber + "' WORKED CORRECTLY!");
            order.orderMessage.internalOrderStatus = "Granite Call SUCCESSFUL";
            order.save();
            Global.showUserMessage("Action performed: Order has been accepted!");
            this.searchAction();
      }
      else {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + "<RE-SUBMIT 'IN HOLD' ORDER> WITH 'ORDER #': '" + input.message.OrderNumber + "' NOT ARCHIVED... Order Rejected By Granite [" + order.orderACK.ErrorDescription + "]...");
            Global.logDebug("<RE-SUBMIT 'IN HOLD' ORDER> WITH 'ORDER #': '" + input.message.OrderNumber + "' NOT ACCEPTED... Order Rejected By Granite [" + order.orderACK.ErrorDescription + "]...");
            order.orderMessage.internalOrderStatus = "Order Rejected By Granite";
            order.save();
            Global.showUserMessage("Action NOT performed: " + order.orderACK.ErrorDescription + "!");
            this.searchAction();
      }

var selectList = this.result.selected;

      if (selectList != null && selectList.length > 0) {
            if (selectList.length == 1) {

                  var item = selectList[0];

                  // Before performing the request check the following:
                  // - the 'In Hold' value must be true;
                  if (!item.inHold){
                        Global.throwException("Action not performed: the selected order is not 'In Hold'!");
                  }
                  else{
                        // - the order must have a failure from Granite
                        if (item.internalOrderStatus == "Granite Communication FAILED" ||
                              item.internalOrderStatus == "Granite Call FAILURE" ||
                              item.internalOrderStatus == "Order Rejected By Granite") {

                              var window = new Document("ds_ws:generic_CheckWindow");
                              window.text = "Re-submit request for 'In Hold' order with Order # '" + item.orderNumber + "', are you sure?";
                              return window;
                        }
                        else{
                              Global.throwException("Action not performed: the selected order has been already accepted by Granite!");
                        }
                  }
            }
            else{
                  Global.throwException("Action not performed: please select just one order!");
            }
      }
      else{
            Global.throwException("Action not performed: no order has been selected!");
      }

var result = this.finder_ResubmitInHoldOrder();
if(result instanceof Finder) {
  Global.runContentDisplayScript(result.searchDocument, "displayScript");
} else {
  Global.runContentDisplayScript(this, "displayScript");
}

return result;
