script.script:
// search child orders and generate, for all of them, the Granite JMS orderAck storing it in Granite_Event_Storage
var children = ds_ws.searchOrderMessageHomeByParentOrderNumber(orderNumber);
if (children != null) {
    for(var i=0; i<children.length; i++) {
        var childOrder = Order.getOrderById(children[i].orderId);

        var orderAcknowledgement_el = new DataStructure("ds_jms.orderAcknowledgement_el");
        orderAcknowledgement_el.orderNumber = childOrder.orderMessage.orderNumber;
        orderAcknowledgement_el.remarks = null;
        orderAcknowledgement_el.xngOrderNumber = childOrder.orderMessage.orderNumber;
        orderAcknowledgement_el.businessUnit = childOrder.orderMessage.businessUnit;
        orderAcknowledgement_el.parentOrderNumber = orderNumber;

        ds_ws.storeReceivedGraniteEvent(orderAcknowledgement_el, "OCUA");
    }
}

return;
