var result = null;

if (eventType != null) {

      var input = new Document("ds_ws:searchGraniteEventStorage");
      input.eventType = eventType;

      var output = Finder.runFinder("ds_ws:findNextStoredGraniteEvent", "select", input);

      if (output != null && output.size > 0) {
            result = output[0];
      }
}

return result;
