var result = null;

if (orderNumber != null) {

      var input = new Document("ds_ws:search_orderMessage");
      input.orderNumber = orderNumber;

      var output = Finder.runFinder("ds_ws:findOrderMessageByOrderNumber", "select", input);

      if (output != null && output.size == 1) {
            result = output[0];
      }
      else if(output != null && output.size > 1){
            // Customized exception AE0002:
            // - Inconsistent environment: found more then one "?" document with the same "?" key!
            Global.throwException("AE0002", ["Order Message", "Order #"]);
      }
}

return result;
