script.script:
var result = null;

if (parentOrderNumber != null &&
      plateId != null) {

      var input = new Document("ds_ws:search_orderMessage");
      input.parentOrderNumber = parentOrderNumber;
      input.commonPlateId = plateId;

      var output = Finder.runFinder("ds_ws:findOrderMessageHomeByParentOrderNumberAndPlateId", "select", input);

      if (output != null && output.size == 1) {
            result = output[0];
      }
}

return result;
