var result = null;

if (serviceType != null) {

      var input = new Document("ds_ws.serviceType_CctTypeToGraniteCctType");
      input.serviceType = serviceType;
      input.cctType = cctType;

      var output = Finder.runFinder("ds_ws.findGraniteCctTypeForReceivedServiceType_CctType", "select", input);

      if (output != null && output.size > 0) {
            result = output.start.graniteCctType;
      }
      else {
            // According to check with Mick Winters, Default Value is ALWAYS "COPPER"
            result = "COPPER";
      }
}

return result;
