var result = null;

if (eventId != null) {

      var input = new Document("ds_ws:searchGraniteEventStorage");
      input.cwDocId = eventId;

      var output = Finder.runFinder("ds_ws:findStoredGraniteEvents", "select", input);

      if (output != null && output.size > 0) {
            result = output[0];
      }
}

return result;
