script.script:
var result = null;

if (woInstId != null) {

   var input = new Document("granite:search_INST_ID");
   input.INST_ID = woInstId;

   var output = Finder.runFinder("granite:findTasksInGraniteByWO_INST_ID", "select", input);

   if (output != null && output.size > 0) {
      result = output[0];
   }
}

return result;
