method.script:
var debug      = " - OCUA Global Process [Send Event To EAI] - ";
var resultType = "OK";

var receivedEvent = ds_ws.readStoredEventData(this.process.processDocument.eventDocId, "ds_jms:orderAcknowledgement_el");
if(receivedEvent != null) {
    var inputSOAP = new DataStructure("ifEAI_OCUA_17122008:processRequest_el");

    var userData1 = receivedEvent.orderNumber;
    var userData2 = null;

    // - mapping the input JMS message vs. input SOAP message
    inputSOAP.OrderCreateUpdateAck.OrderNumber = receivedEvent.orderNumber;
    inputSOAP.OrderCreateUpdateAck.WorkOrder = receivedEvent.xngOrderNumber;
    inputSOAP.OrderCreateUpdateAck.BusinessUnit = receivedEvent.businessUnit;
    // removed according to Vlad email - 2011/06/19
    // inputSOAP.OrderCreateUpdateAck.CircuitId = receivedEvent.circuitId;

    if (receivedEvent.businessUnit == "Home") {
        if(receivedEvent.parentOrderNumber == null){
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + " SEARCHING ParentOrderNumber USING " + receivedEvent.orderNumber);
            var orderMessage = ds_ws.searchOrderMessageHomeByOrderNumber(receivedEvent.orderNumber);
            if (orderMessage != null && orderMessage.parentOrderNumber != null) {
                // - replacing the Order Number
                inputSOAP.OrderCreateUpdateAck.OrderNumber = orderMessage.parentOrderNumber;
                userData1 = orderMessage.parentOrderNumber;
                userData2 = receivedEvent.orderNumber;
            }
            else {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "WARNING: PARENT ORDER NOT FOUND FOR ORDER # " + receivedEvent.orderNumber + "...");
            }
        }
        else {
            inputSOAP.OrderCreateUpdateAck.OrderNumber = receivedEvent.parentOrderNumber;
            userData1 = receivedEvent.parentOrderNumber;
            userData2 = receivedEvent.orderNumber;
        }
    }


    try {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "INVOKING INTERFACE...");
        var outputSOAP = Global.invokeInterface("ifEAI_OCUA_17122008:OCUA", "processRequest", inputSOAP);

        var logMsgId = UserProfile.getMsgLogId();
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "]" + debug + "Adding UserData_1 = " + userData1 + " to id = " + logMsgId);

        UserProfile.setMsgLogData(userData1, userData2, null, logMsgId);

        if(outputSOAP != null && outputSOAP.length == 1) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "RESULT: " + outputSOAP[0].toXML() + "...");
            resultType = "OK";
        }
        else {
            if(outputSOAP == null) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "outputSoap is null....");
                resultType = "NOT_SUCCESS_EAI";
            }
            else {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "Received " + outputSOAP.length + " items from EAI");
                for(var i=0; i<outputSOAP.length; i++) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "Received[" + i + "]: " + outputSOAP[i].toXML());
                }
                resultType = "OK";

                // ignoring the result of the invocation ...
                // resultType = "NOT_SUCCESS_EAI";
            }
        }
    }
    catch (exc){
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "EXCEPTION: " + exc.message);

        if(exc.message.indexOf("XE0023") >= 0) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "I/F getDelay = " + Global.getInterfaceDelay(this.process.processDocument.interfaceName));
            resultType = "NOT_AVAILABLE_EXPD";
        }
        else {
            resultType = "NOT_AVAILABLE_EAI";
        }
    }
}

debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "]" + debug + "resultInvoke: " + resultType + "...");


if(resultType == "OK") {
    ds_ws.deleteStoredEvent(this.process.processDocument.eventDocId);
}
else {
    ds_ws.updateLastSentDateInStoredEvent(this.process.processDocument.eventDocId);
}

this.process.processDocument.resultType = resultType;

var found = false;
var result = ds_ws.searchFirstStoredGraniteEventByType(this.process.processDocument.eventType);
if(result != null) {
      this.process.processDocument.eventDocId = result.cwDocId;
      found = true;

debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] - OCUA Global Process - Found new event with id " + this.process.processDocument.eventDocId);

      }

      return (found);

debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] Start Global for OCUA...");


      this.process.processDocument.eventType  = "OCUA";
      this.process.processDocument.interfaceName = "ifEAI_OCUA_17122008:OCUA";
      this.process.processDocument.eventDocId = null;
      this.process.processDocument.resultType = null;
