method.script:
var allGeneratedSIUFoundList = Finder.runFinder("graniteReport.findSIUDataForHBU", "select", null);

if(allGeneratedSIUFoundList != null && allGeneratedSIUFoundList.length > 0) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - Found " + allGeneratedSIUFoundList.length + " records to process");

    for(var i=0; i<allGeneratedSIUFoundList.length; i++) {
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - Processing record #" + i);

        var siuEvent = new DataStructure("ds_jms.siteInformationUpdate_el");
        siuEvent.businessUnit = "Home";
        siuEvent.ccliCode = allGeneratedSIUFoundList[i].ccliCode;
        siuEvent.city = allGeneratedSIUFoundList[i].city;
        siuEvent.country = allGeneratedSIUFoundList[i].country;
        siuEvent.district = allGeneratedSIUFoundList[i].district;
        siuEvent.region = allGeneratedSIUFoundList[i].region;
        siuEvent.siteID = allGeneratedSIUFoundList[i].siteID;
        siuEvent.siteName = allGeneratedSIUFoundList[i].siteName;
        siuEvent.siteType = allGeneratedSIUFoundList[i].siteType;
        siuEvent.status = allGeneratedSIUFoundList[i].status;
        siuEvent.cityCode = allGeneratedSIUFoundList[i].cityCode;
        siuEvent.districtCode = allGeneratedSIUFoundList[i].districtCode;
        siuEvent.exchangeCode = allGeneratedSIUFoundList[i].exchangeCode;
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - generated event " + siuEvent.toXML());

        ifGranite_jms.manageGraniteMsg_SiteInformationUpdate(siuEvent);
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - sent event to Granite");
    }

    for(var i=0; i<allGeneratedSIUFoundList.length; i++) {
        allGeneratedSIUFoundList[i].deleteItem();
debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - Deleted record #" + i);
    }
}
else {
    debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - No Records Found");
}

var configExecuteGlobal = Global.getConfigVariable("EXECUTE_GLOBAL_GENERATE_SIU", "FALSE").toUpperCase();

debugPrintln("[" + ds_ws.common_currentDateAsStringDDMMYYYYHH24MISS() + "][" + this.process.id + "] GenerateSIUEVENTSFromReport - EXECUTE_GLOBAL_GENERATE_SIU = " + configExecuteGlobal);

return (configExecuteGlobal == "TRUE");
