var serviceStatus = null;
if (ord.serviceParametersList != null) {
      var serviceCounter = 0;
      for (var j = 0; j < ord.serviceParametersList.length; j++) {
            if(ord.serviceParametersList[j].serviceParameters.toBeProcessed) {
                  inputJMS.serviceParameters[serviceCounter].creationDate = ord.serviceParametersList[j].serviceParameters.creationDate;
                  inputJMS.serviceParameters[serviceCounter].oldServiceNumber = ds_ws.common_replaceSpecialCharacter(ord.serviceParametersList[j].serviceParameters.oldServiceNumber);
