script.script:
if(customAttributeId != null) {
    var allPickListValues = configuration.getAllValuesForCustomAttributePickList(customAttributeId);

    if(allPickListValues != null) {
        for (var i=0; i<allPickListValues.length; i++) {
            allPickListValues[i].sequenceNumber = (i+1);
            allPickListValues[i].save();
        }
    }
}

return;
