var sem = theAVM.getSemaphore("TFRNumberLock");
if(!sem){
 sem = theAVM.setSemaphore("TFRNumberLock",0);
}
while( sem > 0){
  sem = theAVM.getSemaphore("TFRNumberLock");
}
theAVM.setSemaphore("TFRNumberLock",0,1);

