var nextQuestionId = null;

if(answerId != null) {
    var finder = new Finder("tfs.findNextQuestionForRejectionAnser");
    finder.searchDocument.cwDocID = answerId;
    var resultList = finder.search();

    if(resultList != null && resultList.length > 0) {
        nextQuestionId = resultList[0].questionId;
    }
}

return nextQuestionId;
