script.script:
var businessUnit = null;

if(userId != null) {
    var docSearch = new Document("cwf_up.UserIDSearch");
    docSearch.userId = userId;

    var resultList = Finder.runFinder("up_priv.getBusinessUnitByUserIdFinder", "select", docSearch);
    if(resultList != null && resultList.length > 0) {
        businessUnit = resultList[0].groupName;
    }
}
return businessUnit;
