script.script:
var groupName = up_priv.getGroupNameByPrivilegeCode(privilegeCode);

var user = null;

if(groupName != null) {
    // invoke procedure
    var arrayList = Global.runDBStoredProcedure("ORDER", "{call assignTokenToNextUserInGroup(?, ?)}", [groupName], "cwt_common.docID",
                                                false, true, "cwt_common.docID", "I-O");
    if(arrayList != null && arrayList.length > 0) {
        user = UserProfile.getUserProfile(arrayList[0].cwDocID);
    }
    else {
        debugPrintln("Next User not found");
    }
}
else {
    debugPrintln("Not able to find a group!");
}

return user;
