script.script:
var site = null;

if(siteInstId != null) {
    var siteFinder = new Finder("granite.siteFinder");
    siteFinder.searchDocument.INST_ID = siteInstId;

    var siteList = siteFinder.search();
    if(siteList != null && siteList.size > 0) {
        site = siteList[0];
    }
}

return site;
