method.script:
if(this.processOrder != null) {

      var orderMetadatatype = this.processOrder.metadataType;
      // checking the type of the order linked to the worklist record

      if(orderMetadatatype.equals("stcw:wholesaleOrder")) {
            // it's order; checking if the order exists in Granite
            var found = stcw.checkOrderExistInGranite(this.processOrder.header.orderNumber);

debugPrintln(stcc.getSysdateForLog() + " - Order '" + this.processOrder.header.orderNumber + "' is in Granite => " + found);

                  if(found) {
                        var cwOrderId = this.processOrder.id;
                        var cwOrder = Order.getOrderById(this.processOrder.id);
                        cwOrder.header.skipSendToGranite = "Y";
                        cwOrder.save();
                  }
            }
      }

      return;
